const progress=document.getElementById("progress"),prev=document.getElementById("prev"),next=document.getElementById("next"),circles=document.querySelectorAll(".circle");let currentActive=1;function update(){circles.forEach((e,t)=>{t<currentActive?e.classList.add("active"):e.classList.remove("active")});const e=document.querySelectorAll(".active");progress.style.width=(e.length-1)/(circles.length-1)*100+"%",1===currentActive?prev.disabled=!0:currentActive===circles.length?next.disabled=!0:(prev.disabled=!1,next.disabled=!1)}next.addEventListener("click",()=>{currentActive++,currentActive>circles.length&&(currentActive=circles.length),update()}),prev.addEventListener("click",()=>{currentActive--,currentActive<1&&(currentActive=1),update()});const panels=document.querySelectorAll(".panel");function removeActiveClasses(){panels.forEach(e=>{e.classList.remove("active")})}panels.forEach(e=>{e.addEventListener("click",()=>{removeActiveClasses(),e.classList.add("active")})});